            var sut = new Conv2DLayer(2, 2, 2, 1, 0, 0, Activation.Undefined);
            GradientCheckTools.CheckLayer(sut, inputWidth, inputHeight, inputDepth, batchSize, 1e-4f, new Random(21));
        }

